settings.default:
/* Base button styles using CSS variables (set by JavaScript) */
/* Loading state for user interactions */
.swym-loading .swym-button-content button,
.swym-loading .swym-button-content [role="button"],
.swym-loading .swym-button-content .btn,
.swym-loading .swym-button-content a {
  opacity: 0.6;
  cursor: not-allowed;
  pointer-events: none;
}
.swym-button-content > button,
.swym-button-content > [role="button"],
.swym-button-content > span,
.swym-button-content > a {
  color: #fff;  
  background: #000;
  border: 1px solid #000;
  font-family: inherit;
  padding: 12px 16px;
  font-size: 14px;
  font-weight: normal;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: all 0.2s ease;
  text-align: center;
  pointer-events: auto;
  min-height: 45px;
  width: 100%;
  letter-spacing: normal;
}

/* Focus styles for accessibility */
.swym-button-content button:focus-visible,
.swym-button-content [role="button"]:focus-visible,
.swym-button-content .btn:focus-visible,
.swym-button-content a:focus-visible {
  outline: 2px solid currentColor;
  outline-offset: 2px;
}
/* Disabled state styles */
.swym-button-content button:disabled,
.swym-button-content [role="button"][aria-disabled="true"],
.swym-button-content .btn:disabled,
.swym-button-content a[aria-disabled="true"] {
  opacity: 0.5;
  cursor: not-allowed;
  pointer-events: none;
}
/* Product image position styling (top-left default) */
.swym-atw-btn-wrapper-for-img.swym-btn-position-product-img {
  position: absolute;
  top: 10px;
  left: 10px;
  z-index: 10;
  width: fit-content!important;
}
.swym-custom-pdp-button.swym-btn-position-product-title {
  width: fit-content;
}
.swym-button-content.swym-btn-custom-position {
  margin-top: 8px;
} 
.swym-button-content button.swym-icon-text svg {
  margin-right: 5px;
}
/* ACCESSIBILITY IMPROVEMENTS */
@media (prefers-reduced-motion: reduce) {
  .swym-button-content,
  .swym-button-content button,
  .swym-button-content [role="button"],
  .swym-button-content .btn,
  .swym-button-content a {
    transition: none;
    animation: none;
  }  
  .swym-button-content.swym-initialized button:hover,
  .swym-button-content.swym-initialized [role="button"]:hover,
  .swym-button-content.swym-initialized .btn:hover,
  .swym-button-content.swym-initialized a:hover {
    transform: none;
  }
}
/* HIGH CONTRAST MODE SUPPORT */
@media (prefers-contrast: high) {
  .swym-atw-btn-wrapper-for-img .swym-button-content button,
  .swym-atw-btn-wrapper-for-img .swym-button-content [role="button"],
  .swym-atw-btn-wrapper-for-img .swym-button-content .btn,
  .swym-atw-btn-wrapper-for-img .swym-button-content a {
    border: 2px solid;
    background: #fff;
  }
}
